<?php

namespace Depot\BaseBundle\Repository;
use Depot\BaseBundle\Entity\Depottype;
use Pagerfanta\Adapter\DoctrineORMAdapter;
use Pagerfanta\Pagerfanta;


/**
 * DepottypeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class DepottypeRepository extends \Doctrine\ORM\EntityRepository
{
	public function querysearch($site)
	{
		return $this->getEntityManager()
            ->createQuery('
                SELECT d
                FROM DepotBaseBundle:Depottype d
                WHERE d.site = :site
                ORDER BY d.id DESC
            ')->setParameter('site', $site)
        ;
	}

	public function page($site,$page = 1)
	{
		$paginator = new Pagerfanta(new DoctrineORMAdapter($this->querysearch($site), false));
        $paginator->setMaxPerPage(Depottype::NUM_ITEMS);
        $paginator->setCurrentPage($page);

        return $paginator;
	}

}
